Rune.initLogic({minPlayers:1,maxPlayers:4,setup:e=>{const t=Object.fromEntries(e.map(r=>[r,5]));return{gameDice:Array.from({length:5},()=>Math.floor(Math.random()*6)+1),diceCount:t,currentPlayerIndex:0,gameOver:!1}},actions:{updateDiceCount:({playerId:e,amount:t},{game:n})=>{if(n.diceCount[e]===void 0)throw Rune.invalidAction();n.diceCount[e]+=t},rollDice:({nextIndex:e,numDice:t},{game:n})=>{n.gameDice=Array.from({length:t},()=>Math.floor(Math.random()*6)+1),n.gameOver||(n.currentPlayerIndex=e)},nextPlayer:({nextIndex:e},{game:t})=>{t.currentPlayerIndex=e}},events:{playerJoined:(e,{game:t})=>{t.diceCount[e]=5},playerLeft(e,{game:t}){delete t.diceCount[e]}}});
