declare global {
    var ReactNativeWebView: {
        postMessage: (data: string) => void;
    } | undefined;
}
type GameToClient = {
    type: "INIT";
    version: string;
} | {
    type: "ERR";
    gamePlayUuid?: string;
    errMsg: string;
} | {
    type: "WARNING";
    gamePlayUuid: string;
    msg: string;
    data?: unknown;
} | {
    type: "WINDOW_ERR";
    err: {
        msg: string;
        filename: string;
        lineno: number;
        colno: number;
    };
} | {
    type: "WINDOW_ALERT";
    message: string;
} | {
    type: "BROWSER_INITIAL_OVERLAY_CLICKED";
} | {
    type: "BROWSER_IFRAME_LOADED";
};
type ClientToGame = {
    type: "setForceMuteStatus";
    muted: boolean;
};

type GameToClientSingle = GameToClient | {
    type: "GAME_OVER";
    gamePlayUuid: string;
    score: number;
    challengeNumber: number;
} | {
    type: "SCORE";
    gamePlayUuid: string;
    score: number;
    challengeNumber: number;
};
type ClientToGameSingle = ClientToGame | {
    type: "pauseGame" | "requestScore";
} | {
    type: "playGame" | "restartGame";
    gamePlayUuid: string;
};

declare function getGameToClientMsg(data: unknown): GameToClientSingle | null;
declare function stringifyClientToGameMsg(data: ClientToGameSingle): string;

export { ClientToGameSingle, GameToClientSingle, getGameToClientMsg, stringifyClientToGameMsg };
